@Component({
  selector: 'app-top-bar',
  templateUrl: './top-bar.component.html',
  styleUrls: ['./top-bar.component.css']
})
export class TopBarComponent {
  showFiller = false;
}
<a [routerLink]="['/']">
  <h1>SGL STORE</h1>
</a>

<a routerLink="/cart" class="button fancy-button"><em class="material-icons" mat-button (click)="drawer.toggle()">shopping_cart</em>Checkout</a>
