import { Component } from '@angular/core';
import { products } from '../products';
import Swal from 'sweetalert2'

@Component({
  selector: 'app-information-alert',
  templateUrl: './information-alert.component.html',
  styleUrls: ['./information-alert.component.css']
})
export class InformationAlertComponent {
  products = [...products];

  information(nombre: string, foto: string, precio: number, descripcion: string){

    Swal.fire({
      title: nombre,
      imageUrl: foto,
      imageWidth: 200,
      imageHeight: 100,
      footer: precio,  
      text: descripcion,
    })

  }

}
