import { Component } from '@angular/core';
import { products } from '../products';
import Swal from 'sweetalert2'

@Component({
  selector: 'app-information-alert',
  templateUrl: './information-alert.component.html',
  styleUrls: ['./information-alert.component.css']
})
export class InformationAlertComponent {
  products = [...products];

  information(nombre: string, foto: any, precio: number, descripcion: string){

    Swal.fire({
      title: nombre,
      imageUrl: foto,
      imageWidth: 150,
      imageHeight: 200,
      footer: precio,  
      text: descripcion,
    })

  }

}
<p *ng="let product of products">
    <button  * type="button" (click)="information(product.name, product.photo, product.price, product.description)">
        information
    </button>   
</p>
