import { Component } from '@angular/core';
import { products } from '../products';


@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css'],
  
})
export class ProductListComponent {
  products = [...products];

  share(nombre: string) {
    window.alert('El producto '+ nombre + ' se ha compartido');
  }
  onNotify(){
    window.alert('se le notificara cuando el producto slaga a la ventana')
  }
}

<h2>Products</h2>

<div class="cart-item" *ngFor="let product of products">
    <h3>
        <a [title]="product.name + 'detalles'"
        [routerLink]=" ['/products', product.id] ">
        {{ product.name }}
        </a>
    </h3>
    <p>
        Precio: {{product.price | currency}}
    </p>
    <p  *ngIf="product.description" >
        Description: {{ product.description}}
    </p>

    <button mat-button type="button" (click)="share(product.name)">
        Shared
    </button>

    <app-product-alerts

     [product]="product"
     (notify)="onNotify()" >

    </app-product-alerts>
    <button mat-button 
        [routerLink]=" [ '/products', product.id] "
    > 
    information
    </button>
    
</div>
